import React from "react";
import { Typography, Box } from "@mui/material";
import FormatQuoteIcon from "@mui/icons-material/FormatQuote";
import Testimonial from "./Testimonial";

const testimonialsData = [
  {
    text: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Explicabo, illo rem? Sint itaque praesentium, id molestiae assumenda perspiciatis consectetur repellat.",
    author: {
      name: "Cornelius Iiro",
      title: "CEO of Intellicode",
    },
  },
  {
    text: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Explicabo, illo rem? Sint itaque praesentium, id molestiae assumenda perspiciatis consectetur repellat.",
    author: {
      name: "Jane Doe",
      title: "Designer at CreativeLabs",
    },
  },
  {
    text: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Explicabo, illo rem? Sint itaque praesentium, id molestiae assumenda perspiciatis consectetur repellat.",
    author: {
      name: "John Smith",
      title: "Founder of TechStartup",
    },
  },
];

const About = () => {
  return (
    <Box sx={{ maxWidth: "1300px", my: 10, padding: 4, mx: "auto" }} id="about">
      <Box sx={{ my: 7 }}>
        {testimonialsData.map((testimonial, index) => (
          <Testimonial key={index} testimonialData={testimonial} />
        ))}
      </Box>
    </Box>
  );
};

export default About;
