import React from "react";

import AppBar from "@mui/material/AppBar";
import Box from "@mui/material/Box";
import Toolbar from "@mui/material/Toolbar";
import Typography from "@mui/material/Typography";
import { Link as ScrollLink } from "react-scroll";
import { styled } from "@mui/system";

import johnteacher from "../media/johnteacher.jpg"

const Navbar = () => {
  const CustomBox = styled(Box)(({ theme }) => ({
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    gap: "1rem",
    [theme.breakpoints.down("sm")]: {
      marginTop: "2rem",
    },
  }));

  const CustomToolbar = styled(Box)(({ theme }) => ({
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
    },
  }));

  const CustomLink = styled(ScrollLink)(({ theme }) => ({
    color: "#fff", // Change link color
    textDecoration: "none",
    marginRight: "1rem",
    "&:hover": {
      color: "#00C7FF", // Change hover color
    },
  }));

  return (
    <Box sx={{ flexGrow: 1, px: 5, py: 2, maxWidth: "1200px", mx: "auto" }}>
      <AppBar
        position="static"
        style={{ background: "rgba(0,0,0,0.8)", boxShadow: "none" }} // Change background color
      >
        <CustomToolbar>
          <Box
            sx={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              gap: "1rem",
            }}
          >
            <Box
              component="img"
              sx={{ maxHeight: "60px", maxWidth: "500px", objectFit: "cover" }}
              src={johnteacher}
            />
            <Typography variant="h6" component="div" sx={{ flexGrow: 1, color: "#fff" }}>
              My Portfolio
            </Typography>
          </Box>

          <CustomBox>
            <CustomLink to="home" className="link" smooth>
              Home
            </CustomLink>
            <CustomLink to="experience" className="link" smooth>
              Experience
            </CustomLink>
            <CustomLink to="about" className="link" smooth>
              About
            </CustomLink>
          </CustomBox>
        </CustomToolbar>
      </AppBar>
    </Box>
  );
};

export default Navbar;
