import { Box, Button, styled, Typography } from "@mui/material";
import React from "react";
import { Link } from "react-scroll";
import johnteacher from "../media/johnteacher.jpg";

const Hero = () => {
  const CustomButton = styled(Button)(({ theme }) => ({
    border: "2px solid #00C7FF",
    backgroundColor: "transparent",
    color: "#00C7FF",
    width: "20%",
    borderRadius: "25px",
    "&:hover": {
      border: "2px solid white",
      backgroundColor: "#00C7FF",
      color: "white",
    },
    [theme.breakpoints.down("md")]: {
      width: "35%",
    },
    [theme.breakpoints.down("sm")]: {
      width: "50%",
    },
  }));

  const CustomTitle = styled(Typography)(({ theme }) => ({
    color: "#FFFFFF",
    textAlign: "center",
    [theme.breakpoints.down("md")]: {
      fontSize: "3.5rem",
    },
    [theme.breakpoints.down("sm")]: {
      fontSize: "2.5rem",
    },
  }));

  const CustomSubtitle = styled(Typography)(({ theme }) => ({
    color: "rgba(255, 255, 255, 0.8)",
    textAlign: "center",
    [theme.breakpoints.down("md")]: {
      fontSize: "1.5rem",
    },
    [theme.breakpoints.down("sm")]: {
      fontSize: "1rem",
    },
  }));

  return (
    <Box
      sx={{
        mt: 8,
        mb: 12,
        p: 2,
        maxWidth: "1200px",
        mx: "auto",
      }}
      id="home"
    >
      <Typography
        sx={{ color: "#FFFFFF", textAlign: "center", mb: 2 }}
        variant="h6"
      >
        Hello, I'm John
      </Typography>
      <CustomTitle variant="h1">
        I am a{" "}
        <span style={{ color: "#00C7FF" }}>
          fullstack Software Engineer
        </span>{" "}
        with a background in{" "}
        <span style={{ color: "#00C7FF" }}>
          Petroleum Engineering
        </span>{" "}
        and I enjoy{" "}
        <span style={{ color: "#00C7FF" }}>building</span> and{" "}
        <span style={{ color: "#00C7FF" }}>designing</span> for
        the web.
      </CustomTitle>
      <CustomSubtitle>
        Leveraging my skills in both software engineering and petroleum
        engineering to create innovative solutions.
      </CustomSubtitle>

      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          gap: "1rem",
        }}
      >
        <Box
          component="img"
          sx={{
            height: 200,
            width: 200,
            mt: 4,
            mb: 4,
          }}
          src={johnteacher}
          alt="John Teacher"
        />

        <CustomButton>Contact Me</CustomButton>
      </Box>
    </Box>
  );
};

export default Hero;
