import { Box, Button, styled, Typography } from "@mui/material";
import React from "react";
import { Link } from "react-scroll";
import johnteacher from "../media/johnteacher.jpg";

const Hero = () => {
  const CustomButton = styled(Button)(({ theme }) => ({
    border: "2px solid #1976D2", // Dark blue border
    backgroundColor: "transparent",
    color: "#1976D2", // Dark blue text
    width: "20%",
    borderRadius: "25px",
    "&:hover": {
      border: "2px solid white",
      backgroundColor: "#1976D2",
      color: "white",
    },
    [theme.breakpoints.down("md")]: {
      width: "35%",
    },
    [theme.breakpoints.down("sm")]: {
      width: "50%",
    },
  }));

  const CustomTitle = styled(Typography)(({ theme }) => ({
    color: "#FFFFFF",
    textAlign: "center",
    [theme.breakpoints.down("md")]: {
      fontSize: "3.5rem",
    },
    [theme.breakpoints.down("sm")]: {
      fontSize: "2.5rem",
    },
  }));

  const CustomSubtitle = styled(Typography)(({ theme }) => ({
    color: "rgba(255, 255, 255, 0.8)",
    textAlign: "center",
    [theme.breakpoints.down("md")]: {
      fontSize: "1.5rem",
    },
    [theme.breakpoints.down("sm")]: {
      fontSize: "1rem",
    },
  }));

  const CustomImage = styled(Box)({
    height: "200px",
    width: "200px",
    borderRadius: "10px", // Rounded corners
    objectFit: "cover", // Ensure the image covers the box
  });

  return (
    <Box
      sx={{
        mt: 8,
        mb: 12,
        p: 2,
        maxWidth: "1200px",
        mx: "auto",
      }}
      id="home"
    >
      <Typography
        sx={{ color: "#FFFFFF", textAlign: "center", mb: 2 }}
        variant="h6"
      >
        Hello, I'm John
      </Typography>
      <CustomTitle variant="h1">
        I am a{" "}
        <span style={{ color: "#1976D2" }}>
          fullstack Software Engineer
        </span>{" "}
        with a background in{" "}
        <span style={{ color: "#1976D2" }}>
          Petroleum Engineering
        </span>{" "}
        and I enjoy{" "}
        <span style={{ color: "#1976D2" }}>building</span> and{" "}
        <span style={{ color: "#1976D2" }}>designing</span> for
        the web.
      </CustomTitle>
      <CustomSubtitle>
        Leveraging my skills in both software engineering and petroleum
        engineering to create innovative solutions.
      </CustomSubtitle>

      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          gap: "1rem",
        }}
      >
        {/* Use the CustomImage component */}
        <CustomImage>
          <img
            src={johnteacher}
            alt="John Teacher"
            style={{ height: "100%", width: "100%" }}
          />
        </CustomImage>

        <CustomButton>Contact Me</CustomButton>
      </Box>
    </Box>
  );
};

export default Hero;
