import { Typography, Box, styled } from "@mui/material";
import React from "react";
import { icons } from "../constants/icons";
import Icon from "./Icon";

const Stack = () => {
  const CustomBox = styled(Box)(({ theme }) => ({
    maxWidth: "1300px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    mx: "auto",
    padding: "2rem",
    // background: "#F5F5F5",
    // borderRadius: "10px",
    boxShadow: "0px 4px 8px rgba(0, 0, 0, 0.15)",
    [theme.breakpoints.down("md")]: {
      flexDirection: "column",
    },
  }));

  const StackBox = styled(Box)(({ theme }) => ({
    flex: 1,
    display: "flex",
    justifyContent: "space-around",
    flexWrap: "wrap",
    margin: "2rem",
    [theme.breakpoints.down("md")]: {
      marginTop: "3rem",
      flexDirection: "column",
    },
  }));

  return (
    <CustomBox>
      <Box sx={{ flex: 1 }}>
        <Typography
          variant="h3"
          sx={{ color: "#555", textAlign: "center", mb: 2 }}
        >
          Versatility is Key
        </Typography>
        <Typography
          variant="h4"
          sx={{ color: "#333", textAlign: "center", fontWeight: "bold" }}
        >
          Here's what I can help you with.
        </Typography>
      </Box>

      <StackBox>
        {icons.map((icon, i) => (
          <Box
            key={icon.id}
            sx={{
              display: "flex",
              flexDirection: "column",
              alignItems: "center",
              margin: "1rem",
            }}
          >
            <Icon name={icon.name} src={icon.src} />
            <Typography variant="subtitle2" sx={{ color: "#333", mt: 1 }}>
              {icon.name}
            </Typography>
          </Box>
        ))}
      </StackBox>
    </CustomBox>
  );
};

export default Stack;
