// About.js

import { Box, Button, Typography, styled } from "@mui/material";
import React from "react";
import Testimonial from "./Testimonial";

const testimonialsData = [
  {
    text: "Lorem ipsum dolor sit amet consectetur adipisicing elit. Explicabo, illo rem? Sint itaque praesentium, id molestiae assumenda perspiciatis consectetur repellat.",
    author: {
      name: "Cornelius Iiro",
      title: "CEO of Intellicode",
    },
  },
  // Add more testimonials with different text

];

const About = () => {
  return (
    <Box sx={{ maxWidth: "1300px", my: 10, padding: 3, mx: "auto" }} id="about">
      <CustomTitleBox
        sx={{
          width: "100%",
          display: "flex",
          alignItems: "center",
          justifyContent: "space-around",
        }}
      >
        <Typography
          variant="h4"
          style={{ color: "white", textAlign: "center" }}
        >
          What people say about my work!
        </Typography>
        <div
          style={{
            height: "5px",
            backgroundColor: "#008080",
            width: "50%",
            border: 0,
            borderRadius: "25px",
          }}
        ></div>
      </CustomTitleBox>

      <CustomBox sx={{ my: 7 }}>
        {testimonialsData.map((testimonial, index) => (
          <Testimonial key={index} {...testimonial} />
        ))}
      </CustomBox>

      <div
        style={{
          height: "5px",
          backgroundColor: "#008080",
          width: "100%",
          mx: "auto",
          border: 0,
          borderRadius: "25px",
        }}
      ></div>

      <Typography
        variant="h4"
        sx={{ color: "white", mt: 12, textAlign: "center" }}
      >
        Interested in Working Together?
      </Typography>
      <CustomButton
        variant="outlined"
        onClick={HandleEmail}
        sx={{
          mx: "auto",
          mt: 3,
          mb: 8,
        }}
      >
        Get in Touch
      </CustomButton>
    </Box>
  );
};

export default About;
