import { Box, Button, styled, Typography } from "@mui/material";
import React from "react";
import { Link } from "react-scroll";
import reactImg from "../media/react_svg.svg";

import johnteacher from "../media/johnteacher.jpg"

const Hero = () => {
  const CustomButton = styled(Button)(({ theme }) => ({
    border: "3px solid white", // Change border color
    backgroundColor: "transparent",
    color: "#fff", // Change button text color
    width: "20%",
    borderRadius: "25px",
    "&:hover": {
      border: "3px solid #00C7FF", // Change hover border color
      backgroundColor: "rgba(0, 199, 255, 0.2)", // Change hover background color
    },
    [theme.breakpoints.down("md")]: {
      width: "35%",
    },
    [theme.breakpoints.down("sm")]: {
      width: "50%",
    },
  }));

  const CustomTitle = styled(Typography)(({ theme }) => ({
    color: "#fff", // Change text color
    textAlign: "center",
    [theme.breakpoints.down("md")]: {
      fontSize: "3.5rem",
    },
    [theme.breakpoints.down("sm")]: {
      fontSize: "2.5rem",
    },
  }));

  return (
    <Box
      sx={{
        mt: 8,
        mb: 12,
        p: 2,
        maxWidth: "1200px",
        mx: "auto",
        backgroundColor: "#444", // Change the background color
      }}
      id="home"
    >
      <Typography
        sx={{ color: "#fff", textAlign: "center", mb: 2 }}
        variant="h6"
      >
        Hello, I'm John
      </Typography>
      <CustomTitle variant="h1">
        I am a <span style={{ color: "#00C7FF" }}>fullstack Software Engineer</span> and I enjoy{" "}
        <span style={{ color: "#00C7FF" }}>building</span> and{" "}
        <span style={{ color: "#00C7FF" }}>designing</span> for the web.
      </CustomTitle>

      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          justifyContent: "center",
          gap: "1rem",
        }}
      >
        <Box
          component="img"
          sx={{
            height: 200,
            width: 200,
            mt: 4,
            mb: 4,
            borderRadius: "50%", // Apply border radius to the image
            border: "3px solid #00C7FF", // Apply border color to the image
          }}
          src={johnteacher}
        />

        <CustomButton>Contact Me</CustomButton>
      </Box>
    </Box>
  );
};

export default Hero;
